import React, { useContext } from 'react';
import SearchContext from '../utils/searchContext';

const PathFilters = () => {
  const { setFilters, filters, path_infos, facets } = useContext(SearchContext);
  const { path } = filters;
  if (!path || path.length === 0 || !path_infos) {
    return '';
  }
  const { root, site_name, path_title } = path_infos;
  return (
    <React.Fragment>
      {!facets.sites ||
        (facets.sites.order.length === 0 && (
          <div className="radio">
            <label className={path === root ? 'selected' : ''}>
              <input
                type="radio"
                name="path"
                value={root}
                checked={path === root}
                onChange={() => setFilters({ path: '' })}
                aria-controls="sitesearch-results-list"
              />
              In tutto <strong>{site_name}</strong>
            </label>
          </div>
        ))}
      <div className="radio">
        <label className={path !== root ? 'selected' : ''}>
          <input
            type="radio"
            name="path"
            value={path}
            checked={true}
            onChange={e => setFilters({ path: e.target.value })}
            aria-controls="sitesearch-results-list"
          />
          nella sezione <strong>{path_title}</strong>
        </label>
      </div>
    </React.Fragment>
  );
};

const SitesFilters = () => {
  const {
    setFilters,
    filters,
    facets,
    current_site,
    translations,
  } = useContext(SearchContext);
  if (!facets || !facets.sites || facets.sites.order.length === 0) {
    return '';
  }
  const { path, site_name } = filters;
  const totalResultsFacets = Object.values(facets.sites.values).reduce(
    (acc, site) => acc + site,
    0,
  );

  const allLabel = translations.sites_all_label
    ? translations.sites_all_label
    : 'In all available sites';

  const currentSiteLabel = translations.sites_local_label
    ? translations.sites_local_label
    : 'In this site';
  return (
    <React.Fragment>
      <div className="radio">
        <label
          className={filters.site_name === 'all' ? 'selected text-primary' : ''}
        >
          <input
            type="radio"
            name="site_name"
            value="all"
            checked={!path && filters.site_name == 'all'}
            onChange={e => setFilters({ site_name: e.target.value, path: '' })}
          />
          {allLabel} ({totalResultsFacets})
        </label>
      </div>
      <div className="radio">
        <label
          className={
            site_name === current_site || !site_name
              ? 'selected text-primary'
              : ''
          }
        >
          <input
            type="radio"
            name="site_name"
            value={current_site}
            checked={path ? false : site_name === current_site || !site_name}
            onChange={e => setFilters({ site_name: e.target.value, path: '' })}
          />
          {currentSiteLabel} ({facets.sites.values[current_site] || 0})
        </label>
      </div>
    </React.Fragment>
  );
};

const LocationFilter = () => {
  const { filters, translations, path_infos, facets } = useContext(
    SearchContext,
  );
  const { path } = filters;
  let canShow = false;
  if (path && path.length > 0 && path_infos) {
    canShow = true;
  } else if (facets.sites && facets.sites.order.length > 0) {
    canShow = true;
  }
  if (!canShow) {
    return '';
  }

  return (
    <div className="filter-item">
      <h3>
        <i className="far fa-folder"></i>{' '}
        {translations['Dove'] ? translations['Dove'] : 'Dove'}
      </h3>
      <PathFilters></PathFilters>
      <SitesFilters></SitesFilters>
    </div>
  );
};

LocationFilter.propTypes = {};

export default LocationFilter;
