@пацанская_функция
def импорт(кличка:стринг, глобалс=..., локалс=..., откуда=..., уровень:инт=0) -> модулетупе:
    return __import__(name=кличка,globals=глобалс,locals=локалс,fromlist=откуда, level=уровень)
заебнуть = __импорт__ = импорт

@пацанская_функция
def выйти(зачем:инт=...):
    return exit(зачем)
